import { Fragment } from "react";
import classes from "./whyChooseUs.module.scss";
import typography from "../components/extras/typography.module.scss";
import { gsap } from "gsap";
import { useRef } from "react";

const Reasons = (props) => {
  const headingRef = useRef(null);
  const paraRef = useRef(null);

  const reasonClass = `${classes.reason} ${classes[`reason__${props.id}`]} }`;

  const headerClass = `${typography["heading__quaternary"]} ${classes["reason__head"]}`;
  let animation;

  const animationHoverHandler = () => {
    animation = gsap.to(headingRef.current, {
      scale: 1.2,
      duration: 0.4,
      ease: "power4.out",
    });
  };

  const animationLeaveHandler = () => {
    animation.reverse();
  };

  return (
    <Fragment>
      <article
        ref={props.innerRef}
        className={reasonClass}
        onMouseEnter={animationHoverHandler}
        onMouseLeave={animationLeaveHandler}
      >
        <h4 ref={headingRef} className={headerClass}>
          {props.title}
        </h4>
        <p ref={paraRef} className={classes["reason__para"]}>
          {props.subtitle}
        </p>
      </article>
    </Fragment>
  );
};

export default Reasons;
