import { useRef, useEffect } from "react";
import { gsap } from "gsap";
import { MotionPathPlugin } from "gsap/MotionPathPlugin";
import wave from "../assets/images/hero-background.svg";

gsap.registerPlugin(MotionPathPlugin);

const Wave = () => {
  const waveRef = useRef(null);

  useEffect(() => {
    const wavePath = waveRef.current.querySelector("#wave-path");
    const waveLine = waveRef.current.querySelector("#wave-line");

    gsap.set([wavePath, waveLine], { autoAlpha: 0 });

    gsap
      .timeline({
        defaults: {
          duration: 1,
          ease: "power2.out",
        },
      })
      .to(wavePath, { autoAlpha: 1 })
      .to(waveLine, { autoAlpha: 1 })
      .fromTo(
        waveLine,
        { motionPath: { path: wavePath, align: wavePath } },
        {
          duration: 2,
          ease: "none",
          motionPath: { path: wavePath, align: wavePath, start: 0.5, end: 1 },
        }
      );
  }, []);

  return (
    <svg
      ref={waveRef}
      width="600"
      height="200"
      viewBox="0 0 600 200"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
    >
      <path
        id="wave-path"
        d="M0 100C150 200 350 0 500 100C650 200 850 0 1000 100"
        stroke="#000"
        strokeWidth="2"
      />
      <path
        id="wave-line"
        d="M0 100C150 200 350 0 500 100C650 200 850 0 1000 100"
        stroke="#000"
        strokeWidth="4"
      />
    </svg>
  );
};

export default Wave;
