import classes from "./Header.module.scss";
import typography from "./extras/typography.module.scss";
import Navigation from "./Navigation";
import { Fragment, useState, useRef, useLayoutEffect } from "react";
import { gsap } from "gsap";

const Header = () => {
  const headerRef = useRef(null);
  const [isNavOn, setIsNavOn] = useState(false);

  useLayoutEffect(() => {
    let ctx = gsap.context(() => {
      gsap.from(classes.sidebar, { duration: 2, rotate: 360, opacity: 0 });
    }, headerRef);

    return () => ctx.revert();
  }, []);

  const toggleSetWid = () => {
    setIsNavOn(!isNavOn);
  };

  return (
    <Fragment>
      <header className={classes.header} ref={headerRef}>
        <p
          className={classes.sidebar}
          onClick={toggleSetWid}
          style={{ zIndex: "9999" }}
        >
          &nbsp;
        </p>
        <p style={{ fontSize: "10rem" }} className="test">
          Test
        </p>
        <Navigation isOn={!isNavOn}></Navigation>
        <article className={classes["header__text-box"]}>
          <h1 className={typography["heading__primary"]}>Track IT</h1>
          <p className={classes["header__para"]}>
            Track your buses with Track IT
          </p>
        </article>
      </header>
    </Fragment>
  );
};

export default Header;
