import { Fragment } from "react";
import { HappyOutline } from "react-ionicons";
import classes from "./whyChooseUs.module.scss";
import typography from "../components/extras/typography.module.scss";

const Reasons = (props) => {
  const reasonClass = `${classes.reason} ${classes[`reason__${props.id}`]}`;

  return (
    <Fragment>
      <article>
        <aside className={reasonClass}>
          <h4 className={typography["heading__quaternary"]}>{props.title}</h4>
          <p className={classes["reason__para"]}>{props.subtitle}</p>
        </aside>
      </article>
    </Fragment>
  );
};

export default Reasons;
