import classes from "./whyChooseUs.module.scss";
import typography from "../components/extras/typography.module.scss";
import Reasons from "./Reasons";
import { HappyOutline } from "react-ionicons";

const WhyChooseUs = () => {
  const data = [
    {
      id: 1,
      title: "Modern UI",
      subtitle:
        " Our website is designed with a user-friendly interface that makes the bus tracking experience hassle-free and easy to navigate",
      icon: HappyOutline,
    },
    {
      id: 2,
      title: "Accurate Info",
      subtitle:
        " We provide accurate and up-to-date information on bus schedules and timings, helping you save time and avoid unnecessary delays.",
      icon: HappyOutline,
    },
    {
      id: 3,
      title: "Mobile-Friendly",
      subtitle:
        " Our website is optimized for mobile devices, allowing you to access it on-the-go and stay updated on bus timings and schedules.",
      icon: HappyOutline,
    },
    {
      id: 4,
      title: "Alerts",
      subtitle:
        " We send  notifications to users about changes in bus timings or routes, helping you plan your journey more effectively and avoid last-minute surprises",
      icon: HappyOutline,
    },
    {
      id: 5,
      title: "Multiple Routes",
      subtitle:
        "We cover multiple routes and bus operators, providing you with more options and flexibility in terms of bus timings and fares.",
      icon: HappyOutline,
    },
  ];
  const headingClass = `${typography["heading__secondary"]} ${typography["heading__secondary--grad"]}`;

  return (
    <section className={classes.whyChooseUs}>
      <h2 className={headingClass}>What's more?</h2>
      <div className={classes.reasons}>
        {data.map((d) => {
          return (
            <Reasons
              key={d.id}
              title={d.title}
              subtitle={d.subtitle}
              icon={d.icon}
            />
          );
        })}
      </div>
    </section>
  );
};

export default WhyChooseUs;
